//Write a Java program to find the value of specified expression
package lab;

public class Value_expression {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=(101+0)/3;//question
		System.out.println("(101+0)/3->" + a);
		double b=(3.0e-6*10000000.1);//question
				System.out.println("(3.0e-6*10000000.1)->" +b);//output
				System.out.println("(true && true)->true");//output
				System.out.println("(false && true)->false");//output
				System.out.println("(false && false)||(true && true))->true");//output
				System.out.println("(true && true)&&(false && false)->false" );//output
	}

}
